def _get_drop_r(live_setting_id: int):
    unit_drops = LIVE_SPECIFIC_REWARD.get(live_setting_id)

    if unit_drops:
        return SYSRAND.choice(unit_drops)

    return 0


async def _get_drop_n(context: npps4.idol.BasicSchoolIdolContext):
    global DEFAULT_N_DROPS

    if DEFAULT_N_DROPS is None:
        q = sqlalchemy.select(npps4.db.unit.Unit).where(npps4.db.unit.Unit.rarity == 1)
        result = await context.db.unit.execute(q)
        DEFAULT_N_DROPS = [unit.unit_id for unit in result.scalars()]

    return SYSRAND.choice(DEFAULT_N_DROPS)
